import { inject, Injectable } from '@angular/core';
import { FirebaseApp } from '@angular/fire/app';
import { addDoc, deleteDoc, getDocs, getFirestore, setDoc, writeBatch, doc, collection } from '@angular/fire/firestore';
import { dataObjectConverter } from '../../shared/firebase/firestore-helper';
import { Race } from '../model/race';
import { Series } from '../model/series';

export interface RaceSeriesDetails {
  id: string;
  name: string;
  fleetId: string;
}

@Injectable({
  providedIn: 'root',
})
export class RaceCalendarStoreBase {
  protected readonly firestore = getFirestore(inject(FirebaseApp));  

  protected ref = (id: string) => doc(this.firestore, 'series', id).withConverter(dataObjectConverter<Series>());
  protected readonly seriesCollection = collection(this.firestore, '/series').withConverter(dataObjectConverter<Series>()); 

  /** Add a series retruning a document Id */
  async addSeries(series: Partial<Series>): Promise<string> {
    series.archived = false;
    const ref = await addDoc(this.seriesCollection, series);
    return ref.id;
  }

  async updateSeries(id: string, data: Partial<Series>) {
    await setDoc(this.ref(id), data, { merge: true });
  }

  async deleteSeries(id: string) {
    // Delete races for the series. 
    const racesSnapshot = await getDocs(this.racesCollection(id));

    const batch = writeBatch(this.firestore);
    racesSnapshot.forEach(doc => batch.delete(doc.ref));

    // Delete the series in the batch
    batch.delete(this.ref(id));

    await batch.commit();
  }

  protected raceRef = (seriesId: string, id: string) => doc(this.firestore, `/series/${seriesId}/races`, id).withConverter(dataObjectConverter<Race>());
  protected racesCollection = (seriesId: string) => collection(this.firestore, `/series/${seriesId}/races`).withConverter(dataObjectConverter<Race>());

  async addRace(seriesDetails: RaceSeriesDetails, race: Partial<Race>): Promise<void> {
    race.seriesId = seriesDetails.id;
    race.seriesName = seriesDetails.name;
    race.fleetId = seriesDetails.fleetId;
    race.status = 'Future';
    await addDoc(this.racesCollection(race.seriesId),race);
  }

  async updateRace(seriesId: string, raceId: string, data: Partial<Race>): Promise<void> {
    await setDoc(this.raceRef(seriesId, raceId), data, { merge: true });
  }

  async deleteRace(seriesId: string, race: Race): Promise<void> {
    await deleteDoc(this.raceRef(seriesId, race.id));
  }
}

export function seriesSort(a: Series, b: Series): number {
  if (!a.startDate) {
    return !b.startDate ? 1 : 0;
  }

  if (!b.startDate) {
    return -1;
  }

  const ret = a.startDate.getTime() - b.startDate.getTime();
  if (ret !== 0) {
    return ret;
  } else {
    return a.fleetId.localeCompare(b.fleetId);
  }
}

export function sortRaces(a: Race, b: Race): number {
  const ret = a.scheduledStart.getTime() - b.scheduledStart.getTime();

  if (ret === 0) {
    return a.raceOfDay - b.raceOfDay;
  } else {
    return ret;
  }
}

import { ChangeDetectionStrategy, Component, inject } from '@angular/core';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';
import { FormControl, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { MatButtonModule } from '@angular/material/button';
import { MAT_DIALOG_DATA, MatDialogModule, MatDialogRef } from '@angular/material/dialog';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatRadioModule } from '@angular/material/radio';
import { Race } from '../../race-calender/model/race';
import { format } from 'date-fns';

export interface RaceStartTimeResult {
  mode: 'tod' | 'elapsed';
  startTime: Date;
}

@Component({
  selector: 'app-race-start-time-dialog',
  template: `
    <h2 mat-dialog-title>Set Race Start Time</h2>
    <mat-dialog-content>
      <form [formGroup]="form" class="dialog-form">
        <p>Select the timing method used for this race:</p>
        <mat-radio-group formControlName="mode" class="radio-group">
          <mat-radio-button value="tod">Time of Day (Real Time)</mat-radio-button>
          <mat-radio-button value="elapsed">Stopwatch (Elapsed)</mat-radio-button>
        </mat-radio-group>

        <mat-form-field appearance="outline">
          <mat-label>{{ form.value.mode === 'tod' ? 'Start Time (HH:mm:ss)' : 'Stopwatch Reading at Start (mm:ss)' }}</mat-label>
          <input matInput type="time" step="1" formControlName="time">
        </mat-form-field>
      </form>
    </mat-dialog-content>
    <mat-dialog-actions align="end">
      <button mat-button mat-dialog-close>Cancel</button>
      <button mat-flat-button color="primary" [disabled]="form.invalid" (click)="save()">Set Start Time</button>
    </mat-dialog-actions>
  `,
  styles: [`
    .dialog-form { display: flex; flex-direction: column; gap: 16px; min-width: 350px; }
    .radio-group { display: flex; flex-direction: column; gap: 8px; margin-bottom: 8px; }
  `],
  imports: [MatDialogModule, MatButtonModule, MatFormFieldModule, MatInputModule, MatRadioModule, ReactiveFormsModule],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class RaceStartTimeDialog {
  private dialogRef = inject(MatDialogRef<RaceStartTimeDialog>);
  private data = inject<{ race: Race }>(MAT_DIALOG_DATA);

  readonly form = new FormGroup({
    mode: new FormControl<'tod' | 'elapsed'>(this.data.race.timeInputMode || 'tod', { nonNullable: true }),
    time: new FormControl<string>('', Validators.required),
  });

  constructor() {
    const { race } = this.data;
    const mode = race.timeInputMode || 'tod';

    if (race.actualStart) {
      this.form.controls.time.setValue(format(new Date(race.actualStart), 'HH:mm:ss'));
    }

    this.form.controls.mode.valueChanges.pipe(takeUntilDestroyed()).subscribe(mode => {
      if (mode === 'elapsed' && !this.form.controls.time.value) {
        this.form.controls.time.setValue('00:00');
      }
    });
  }

  save() {
    if (this.form.valid) {
      const { mode, time } = this.form.getRawValue();

      // For both 'tod' and 'elapsed', the start time is stored as a Date on the day of the race.
      // For 'elapsed', the time part represents the duration from midnight.
      const dateStr = new Date(this.data.race.scheduledStart).toDateString();
      const startTime = new Date(`${dateStr} ${time}`);

      this.dialogRef.close({ mode, startTime } as RaceStartTimeResult);
    }
  }
}

