/** Is the competitor considered a starter */
export function isStarter(code: ResultCode): boolean {
  return !['DNC', 'DNS', 'OOD', 'NOT FINISHED'].includes(code);
}

export function isFinishedComp(code: ResultCode): boolean {
  return [
    'OK',
    'SCP',
    'ZFP',
    'XPA',
    'RDG',
    'RDGA',
    'RDGB',
    'RDGC',
    'DPI',
  ].includes(code);
}

