import { inject, Injectable } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { firstValueFrom } from 'rxjs';
import { BoatsDupDialogData, BoatsDuplicatesDialog } from './boats-duplicates-dialog';
import { Boat, BoatsStore } from 'app/boats';

@Injectable({
  providedIn: 'root'
})
export class DuplicateBoatCheck {
  private snackbar = inject(MatSnackBar);
  private dialog = inject(MatDialog);
  private bs = inject(BoatsStore);

  async duplicateCheck(boat: Partial<Boat>): Promise<boolean> {

    const check = checkForDuplicateBoats(boat, this.bs.boats());

    if (check.isDuplicate) {
      this.snackbar.open("Duplicate Boat exists in boat repository", "Dismiss", { duration: 4000 });
      return false;
    } else if (check.matches.length > 0) {
      return await this.showDialog(boat as Boat, check.matches);
    }
    return true;
  }

  async showDialog(boat: Boat, dups: Boat[]): Promise<boolean> {

    const dialogRef = this.dialog.open<BoatsDuplicatesDialog, BoatsDupDialogData>(BoatsDuplicatesDialog, {
      data: { boat: boat, possibleDuplicates: dups },
    });
    return firstValueFrom(dialogRef.afterClosed());
  }
}

/** Checks for duplicate boats, returning a list of boats that
 * match exactly and ones that are similar to 
*/
function checkForDuplicateBoats(newBoat: Partial<Boat>, allBoats: Boat[]): { isDuplicate: boolean, matches: Boat[]; } {
  const possibles = allBoats.filter(
    boat => boat.boatClass === newBoat.boatClass &&
      boat.sailNumber === newBoat.sailNumber);

  const isDuplicate = possibles.find(boat => boat.helm.toLowerCase() === newBoat.helm?.toLowerCase()) !== undefined;

  return { isDuplicate, matches: possibles };
}

import { Injectable, inject } from '@angular/core';
import { ClubService } from 'app/club';
import { Race } from '../../race-calender/model/race';
import { RaceCompetitor } from '../../results-input/model/race-competitor';
import { RaceCompetitorStore } from '../../results-input/services/race-competitor-store';

export interface EntryDetails {
  races: Race[];
  helm: string;
  crew?: string;
  boatClass: string;
  sailNumber: number;
  handicap?: number;
}

@Injectable({
  providedIn: 'root'
})
export class EntryService {
  private clubService = inject(ClubService);
  private raceResultsService = inject(RaceCompetitorStore);

  /** Enter a race 
   * throws a *** exception if the entry is a duplicate. 
   * @
  */
  async enterRaces(details: EntryDetails): Promise<void> {
    let handicap = details.handicap;

    console.log("Calling enter races");

    if (this.isDuplicateEntry(details)) {
      throw new Error("Duplicate entry");
    }

    // Populate handicap based on the classes handicap if not provided
    if (handicap === undefined || handicap === null) {
      const boatClass = this.clubService.club().classes.find(c => c.name === details.boatClass);
      handicap = boatClass?.handicap ?? 0;
    }

    const promises = details.races.map(race => {
      const competitor: Partial<RaceCompetitor> = {
        raceId: race.id,
        seriesId: race.seriesId,
        helm: details.helm,
        crew: details.crew,
        boatClass: details.boatClass,
        sailNumber: details.sailNumber,
        handicap: handicap,
        resultCode: 'NOT FINISHED'
      };

      console.log("Adding competitor");


      return this.raceResultsService.addResult({
        seriesId: race.seriesId,
        raceId: race.id,
      }, competitor);
    });

    await Promise.all(promises);
  }

  /** 
   * Check if a boat (class+sailnumber) is already entered
   * in any of the races being entered.
   * Returns true if a duplicate is found.
 */
  isDuplicateEntry(details: EntryDetails): boolean {
    for (const race of details.races) {
      const dup = this.raceResultsService.selectedCompetitors().find(comp =>
        comp.boatClass === details.boatClass &&
        comp.raceId === race.id &&
        comp.sailNumber == details.sailNumber);
      if (dup) {
        return true;
      }
    }

    return false;
  }

}

import { Injectable, Injector, ProviderToken } from '@angular/core';

@Injectable({ providedIn: 'root' })
export class LazyInject {
   constructor(private injector: Injector) { }

   async get<T>(providerLoader: () => Promise<ProviderToken<T>>): Promise<T> {
      return this.injector.get(await providerLoader());
   }

   /**
    * Simplifies getting a provider from a dynamically imported module.
    * This is more type-safe than the basic `get` as it can infer the return
    * type and validate the provider name at compile time.
    * @param moduleLoader A function that returns a promise to a module, e.g., `() => import('@angular/material/dialog')`.
    * @param providerName The string name of the exported provider class/token from the module, e.g., `'MatDialog'`.
    */
   async getProvider<M, K extends keyof M>(
      moduleLoader: () => Promise<M>,
      providerName: K
   ): Promise<M[K] extends ProviderToken<infer I> ? I : never> {
      const module = await moduleLoader();
      const providerToken = module[providerName] as ProviderToken<any>;
      return this.injector.get(providerToken);
   }
}

