import { ChangeDetectionStrategy, Component, inject, signal, viewChild } from '@angular/core';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { BoatForm } from './boat-form/boat-form';
import { Toolbar } from 'app/shared/components/toolbar';
import { BoatsStore } from '../services/boats.store';
import { DuplicateBoatCheck } from './duplicate-boat-check/duplicate-check-service';
import { Boat } from '../model/boat';

@Component({
  selector: 'app-boat-add',
  imports: [BoatForm, Toolbar],
  changeDetection: ChangeDetectionStrategy.OnPush,
  template: `
   <app-toolbar title="Add Boat" showBack/>
    <app-boat-form (submitted)="submitted($event)" [busy]="busy()"/>
  `,
  styles: [],
})
export class BoatAdd {
  private bs = inject(BoatsStore);
  private router = inject(Router);
  private snackbar = inject(MatSnackBar);
  private dupCheck = inject(DuplicateBoatCheck);

  busy = signal(false);

  readonly form = viewChild.required(BoatForm);

  async submitted(boat: Partial<Boat>) {
    try {
      this.busy.set(true);

      const save = await this.dupCheck.duplicateCheck(boat);

      if (save) {
        await this.bs.add(boat);
        this.router.navigate(["/boats"]);
      }
    } catch (error: any) {
      this.snackbar.open("Error encountered adding Boat", "Dismiss", { duration: 3000 });
      console.log('AddBoat:  Error adding Boat: ' + error.toString());
    } finally {
      this.busy.set(false);
    }


  }

  canDeactivate(): boolean {
    return this.form().canDeactivate();
  }
}

import { ChangeDetectionStrategy, Component, computed, inject, input, signal, viewChild } from '@angular/core';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { BoatForm } from './boat-form/boat-form';
import { Toolbar } from 'app/shared/components/toolbar';
import { BoatsStore } from '../services/boats.store';
import { DuplicateBoatCheck } from './duplicate-boat-check/duplicate-check-service';
import { Boat } from '../model/boat';
import { DialogsService } from 'app/shared/dialogs/dialogs.service';

@Component({
  selector: 'app-boat-edit',
  imports: [BoatForm, Toolbar],
  changeDetection: ChangeDetectionStrategy.OnPush,
  template: `
   <app-toolbar [title]="'Edit Boat - ' + boat().boatClass + '  ' + boat().sailNumber" showBack/>

    <app-boat-form [boat]="boat()" (submitted)="submitted($event)" (deleted)="deleted($event)"></app-boat-form>
  `,
  styles: [],
})
export class BoatEdit {
  private bs = inject(BoatsStore);
  private router = inject(Router);
  private snackbar = inject(MatSnackBar);
  private ds = inject(DialogsService);
  private dupCheck = inject(DuplicateBoatCheck);

  id = input.required<string>();   // Route parameter

  boat = computed(() => this.bs.boats().find(l => l.id === this.id())!);

  busy = signal(false);

  readonly form = viewChild.required(BoatForm);

  async submitted(data: Partial<Boat>) {
    try {
      this.busy.set(true);
      const save = await this.dupCheck.duplicateCheck(data);
      if (save) {
        await this.bs.update(this.id(), data);
        this.router.navigate(["/boats"]);
      }
    } catch (error: any) {
      this.snackbar.open("Error encountered updating boat details", "Dismiss", { duration: 3000 });
      console.log('UpdateBoat. Error updating boat details: ' + error.toString());
    } finally {
      this.busy.set(false);
    }
  }

  async deleted(boat: Boat) {
    const ok = await this.ds.confirm("Delete boat", "Delete boat");
    if (ok) {
      try {
        this.busy.set(true);

        await this.bs.delete(boat.id);
        this.router.navigate(["/boats"]);
      } catch (error: any) {
        this.snackbar.open("Error encountered deleting task", "Error encountered deleting task", { duration: 3000 });
        console.log('UpdateTask. Error deleting task: ' + error.toString());
      } finally {
        this.busy.set(false);
      }
    }
  }

  canDeactivate(): boolean {
    return this.form().canDeactivate();
  }
}

import { ChangeDetectionStrategy, Component, effect, inject, input, output, signal } from '@angular/core';
import { FormControl, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { MatButtonModule } from '@angular/material/button';
import { MatCheckboxModule } from '@angular/material/checkbox';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { SubmitButton } from 'app/shared/components/submit-button';
import { DeleteButton } from 'app/shared/components/delete-button';
import { ClubService } from 'app/club';
import { Boat } from 'app/boats';

@Component({
  selector: 'app-boat-form',
  templateUrl: './boat-form.html',
  styleUrl: 'boat-form.scss',
  standalone: true,
  imports: [ReactiveFormsModule, MatFormFieldModule, MatInputModule,
    MatSelectModule, MatButtonModule, MatCheckboxModule, SubmitButton, DeleteButton],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class BoatForm {

  cs = inject(ClubService);

  boat = input<Boat | undefined>();
  busy = input<boolean>(false);
  submitted = output<Partial<Boat>>();
  deleted = output<Boat>();

  form = new FormGroup({
    boatClass: new FormControl('', { validators: [Validators.required] }),
    sailNumber: new FormControl<Number>(0, { validators: [Validators.required, Validators.min(0)] }),
    name: new FormControl(''),
    helm: new FormControl(''),
    crew: new FormControl(''),
    isClub: new FormControl<boolean>(false),
  });

  constructor() {
    effect(() => {
      if (this.boat()) {
        this.form.patchValue(this.boat()!);
      }
    });
  }

  submit() {
    const output: Partial<Boat> = this.form.getRawValue() as Partial<Boat>;
    this.submitted.emit(output);
    this.form.reset();
  }

  public canDeactivate(): boolean {
    return !this.form.dirty;
  }
}

